"use strict";
var plugin = {};
plugin.parse = function(data, callback) {
	if (data && data.postData && data.postData.content) {
		data.postData.content = data.postData.content.replace(/((?:https?:\/\/)?trello\.com\/c\/(\w{8})(?:\/(?:\w*-?)*))/gi, "<a class='integration trello-link' href='$1'>$2</a>");
	}
	callback(null, data);
};
module.exports = plugin;
