"use strict";
var plugin = {};
plugin.parse = function(data, callback) {
	if (data && data.postData && data.postData.content) {
		data.postData.content = data.postData.content.replace(/((?:https?:\/\/)?trello\.com\/c\/(\w{8})(?:\/(?:\w*-?)*)?)/gi, "<a title='Trello: $2' class='integration trello-link' href='$1'>$2</a>");
		data.postData.content = data.postData.content.replace(/(?:\[((?:[a-zA-ZÀ-ÿ0-9\-\'\&]+\s*)+)\])?\(?((?:https?:\/\/)?help\.unisyl\.tk\/knowledge_base\/topics\/(?:(?:\w+-?)+))\)?/gi, '<a title="$1" class="integration help-desk" href="$2">$1</a>');
	}
	callback(null, data);
};
module.exports = plugin;
